import React, { useState, useEffect } from 'react';
import './About.css';

const About = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [needsCarousel, setNeedsCarousel] = useState(false);

  // 图片数组
  const images = [
    '/images/card-1.jpg',
    '/images/card-2.jpg', 
    '/images/card-3.jpg',
    '/images/card-4.jpg'
  ];

  // 检测屏幕大小，决定是否需要轮播
  useEffect(() => {
    const checkScreenSize = () => {
      const screenWidth = window.innerWidth;
      // 如果屏幕宽度小于768px，启用轮播
      setNeedsCarousel(screenWidth < 768);
    };

    checkScreenSize();
    window.addEventListener('resize', checkScreenSize);
    return () => window.removeEventListener('resize', checkScreenSize);
  }, []);

  // 轮播控制函数
  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + images.length) % images.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <section className="about section" id="about">
      <div className="container">
        <h2 className="section-title">Meet TechFocus</h2>
        <div className="section-line"></div>

        {/* 图片展示区域 - 填满网页宽度 */}
        <div className="about-hero-area">
          <div className="image-container">
            {needsCarousel ? (
              // 移动端轮播模式
              <div className="carousel-mode">
                <div className="carousel-wrapper">
                  <div 
                    className="carousel-slides"
                    style={{ transform: `translateX(-${currentSlide * 100}%)` }}
                  >
                    {images.map((image, index) => (
                      <div key={index} className="carousel-slide">
                        <img 
                          src={image} 
                          alt={`Slide ${index + 1}`}
                          onError={(e) => {
                            e.target.style.display = 'none';
                          }}
                        />
                      </div>
                    ))}
                  </div>
                </div>

                {/* 轮播控制按钮 */}
                <button className="carousel-btn carousel-btn-prev" onClick={prevSlide}>
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                    <path d="M15 18L9 12L15 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                </button>
                <button className="carousel-btn carousel-btn-next" onClick={nextSlide}>
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                    <path d="M9 18L15 12L9 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                </button>

                {/* 指示器 */}
                <div className="carousel-indicators">
                  {images.map((_, index) => (
                    <button
                      key={index}
                      className={`indicator ${index === currentSlide ? 'active' : ''}`}
                      onClick={() => goToSlide(index)}
                    />
                  ))}
                </div>
              </div>
            ) : (
              // 桌面端同时显示所有图片
              <div className="grid-mode">
                {images.map((image, index) => (
                  <div key={index} className="image-item">
                    <img 
                      src={image} 
                      alt={`Image ${index + 1}`}
                      onError={(e) => {
                        e.target.style.display = 'none';
                      }}
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        <div className="about-content">
          {/* 文字内容 */}
          <div className="about-text">
            <p className="about-description">
              TechFocus, a SBA 8(a) and WOSB certified IT hardware provider, has built a strong track record of delivering exceptional service and value to our clients over the years. With over $25 million in sales, we demonstrate our commitment to customer satisfaction and solidify our position as a trusted partner in the IT hardware industry.
            </p>
            <p className="about-description">
              Holding a GSA MAS contract with SIN 33411, we showcase our ability to meet stringent federal requirements and supply products to government agencies.
            </p>
            <p className="about-description">
              As a leading provider of technology products, TechFocus has become a trusted source for IT hardware products and solutions. We offer a comprehensive range of electronic and hardware items, such as computers, monitors, peripheral products, and networking equipment, making it easy for customers to acquire the technology they need.
            </p>
            <p className="about-mission">
              Our mission is to exceed our government customers' expectations while maintaining an unparalleled level of service in the professional services industry.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
