import React, { useState, useEffect } from 'react';
import './ContractVehicles.css';

const ContractVehicles = () => {
  // 默认图片配置
  const defaultImages = [
    {
      id: 1,
      src: '/images/c1.jpg',
      alt: '合同车辆 1',
      title: 'Contract Vehicle 1'
    },
    {
      id: 2,
      src: '/images/c2.png',
      alt: '合同车辆 2',
      title: 'Contract Vehicle 2'
    },
    {
      id: 3,
      src: '/images/c3.jpg',
      alt: '合同车辆 3',
      title: 'Contract Vehicle 3'
    },
    {
      id: 4,
      src: '/images/c4.png',
      alt: '合同车辆 4',
      title: 'Contract Vehicle 4'
    }
  ];

  // 状态管理
  const [contractImages, setContractImages] = useState(defaultImages);
  const [uploadingIndex, setUploadingIndex] = useState(null);

  // 从本地存储加载用户上传的图片
  useEffect(() => {
    const savedImages = localStorage.getItem('contractVehicleImages');
    if (savedImages) {
      try {
        const parsedImages = JSON.parse(savedImages);
        setContractImages(parsedImages);
      } catch (error) {
        console.error('加载保存的图片失败:', error);
      }
    }
  }, []);

  // 处理图片上传
  const handleImageUpload = (index, event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('image/')) {
      setUploadingIndex(index);

      const reader = new FileReader();
      reader.onload = (e) => {
        const newImages = [...contractImages];
        newImages[index] = {
          ...newImages[index],
          src: e.target.result,
          isCustom: true
        };

        setContractImages(newImages);
        // 保存到本地存储
        localStorage.setItem('contractVehicleImages', JSON.stringify(newImages));
        setUploadingIndex(null);
      };

      reader.readAsDataURL(file);
    } else {
      alert('请选择有效的图片文件！');
    }
  };

  // 重置为默认图片
  const resetToDefault = (index) => {
    const newImages = [...contractImages];
    newImages[index] = { ...defaultImages[index] };
    setContractImages(newImages);
    localStorage.setItem('contractVehicleImages', JSON.stringify(newImages));
  };

  // 重置所有图片
  const resetAllImages = () => {
    setContractImages(defaultImages);
    localStorage.removeItem('contractVehicleImages');
  };

  return (
    <section className="contract-vehicles section" id="contract">
      <div className="container">
        <div className="section-header">
          <h2 className="section-title">Contract Vehicles</h2>
          <button 
            className="reset-all-btn"
            onClick={resetAllImages}
            title="重置所有图片为默认图片"
          >
            🔄 重置所有
          </button>
        </div>
        <div className="section-line"></div>

        <div className="contract-grid">
          {contractImages.map((image, index) => (
            <div key={image.id} className="contract-card">
              <div className="contract-image-wrapper">
                {uploadingIndex === index ? (
                  <div className="uploading-placeholder">
                    <div className="loading-spinner"></div>
                    <span>上传中...</span>
                  </div>
                ) : (
                  <>
                    <img 
                      src={image.src} 
                      alt={image.alt}
                      className="contract-image"
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'block';
                      }}
                    />
                    <div className="contract-placeholder backup" style={{display: 'none'}}>
                      <span>图片加载失败</span>
                    </div>
                  </>
                )}

                {/* 图片操作按钮 */}
                <div className="image-controls">
                  <label className="upload-btn" title="上传新图片">
                    📷
                    <input 
                      type="file" 
                      accept="image/*"
                      onChange={(e) => handleImageUpload(index, e)}
                      style={{display: 'none'}}
                    />
                  </label>

                  {image.isCustom && (
                    <button 
                      className="reset-btn"
                      onClick={() => resetToDefault(index)}
                      title="恢复默认图片"
                    >
                      ↩️
                    </button>
                  )}
                </div>
              </div>

              <h3 className="contract-title">{image.title}</h3>
            </div>
          ))}
        </div>

        <div className="help-text">
          <p>💡 <strong>使用说明：</strong></p>
          <ul>
            <li>点击 📷 图标可以上传新图片替换当前图片</li>
            <li>点击 ↩️ 图标可以恢复为默认图片</li>
            <li>支持 JPG、PNG、WebP 等格式</li>
            <li>建议图片尺寸比例为 4:3 (横向)</li>
          </ul>
        </div>
      </div>
    </section>
  );
};

export default ContractVehicles;
