import React from 'react';
import './Logo.css';

const Logo = () => {
  // 配置选项
  const logoImagePath = '/images/logo.svg'; // SVG 图片路径
  const useImageLogo = false; // 设置为 true 使用图片，false 使用内嵌 SVG
  const useCustomSVG = true; // 设置为 true 使用自定义 SVG 代码

  // 自定义 SVG Logo（将你的 SVG 代码粘贴在这里）
  const CustomSVGLogo = () => (
    <svg width="120" height="60" viewBox="0 0 120 60" fill="none" xmlns="http://www.w3.org/2000/svg">
      {/* 将你的 SVG 内容粘贴在这里 */}
      {/* 例如: <path d="..." fill="#..."/> */}

      {/* 临时占位文本 - 替换为你的 logo */}
      <text x="60" y="35" textAnchor="middle" fill="#0A3161" fontSize="14" fontFamily="Inter">
        Your Logo Here
      </text>
    </svg>
  );

  return (
    <div className="logo">
      {useCustomSVG ? (
        // 使用自定义 SVG 代码（最佳性能）
        <CustomSVGLogo />
      ) : useImageLogo ? (
        // 使用 SVG 图片文件
        <img 
          src={logoImagePath} 
          alt="TechFocus Logo" 
          className="logo-image"
          onError={(e) => {
            console.warn('Logo 图片加载失败，请检查图片路径:', logoImagePath);
            e.target.style.display = 'none';
            e.target.nextSibling.style.display = 'block';
          }}
        />
      ) : (
        // 备用选项：如果都关闭，显示文本
        <div className="logo-fallback" style={{display: 'block'}}>
          TechFocus
        </div>
      )}

      {/* 备用文本 logo */}
      <div className="logo-fallback" style={{display: 'none'}}>
        TechFocus
      </div>

      <span className="logo-text">TechFocus</span>
    </div>
  );
};

export default Logo;
