import React, { useRef } from 'react';
import './About.css';

const About = () => {
  const scrollContainerRef = useRef(null);

  // 图片数组
  const images = [
    '/images/card-1.jpg',
    '/images/card-2.jpg', 
    '/images/card-3.jpg',
    '/images/card-4.jpg'
  ];

  // 滚动控制函数
  const scrollLeft = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollBy({
        left: -300,
        behavior: 'smooth'
      });
    }
  };

  const scrollRight = () => {
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollBy({
        left: 300,
        behavior: 'smooth'
      });
    }
  };

  return (
    <section className="about section" id="about">
      <div className="container">
        <h2 className="section-title">Meet TechFocus</h2>
        <div className="section-line"></div>

        {/* 滚动图片展示区域 - 填满网页宽度 */}
        <div className="about-hero-area">
          <div className="scroll-container">
            {/* 左滚动按钮 */}
            <button className="scroll-btn scroll-btn-left" onClick={scrollLeft}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                <path d="M15 18L9 12L15 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </button>

            {/* 滚动图片容器 */}
            <div className="images-scroll-wrapper" ref={scrollContainerRef}>
              {images.map((image, index) => (
                <div key={index} className="scroll-image-item">
                  <img 
                    src={image} 
                    alt={`Image ${index + 1}`}
                    onError={(e) => {
                      e.target.style.display = 'none';
                    }}
                  />
                </div>
              ))}
            </div>

            {/* 右滚动按钮 */}
            <button className="scroll-btn scroll-btn-right" onClick={scrollRight}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
                <path d="M9 18L15 12L9 6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </button>
          </div>
        </div>

        <div className="about-content">
          {/* 文字内容 */}
          <div className="about-text">
            <p className="about-description">
              TechFocus, a SBA 8(a) and WOSB certified IT hardware provider, has built a strong track record of delivering exceptional service and value to our clients over the years. With over $25 million in sales, we demonstrate our commitment to customer satisfaction and solidify our position as a trusted partner in the IT hardware industry.
            </p>
            <p className="about-description">
              Holding a GSA MAS contract with SIN 33411, we showcase our ability to meet stringent federal requirements and supply products to government agencies.
            </p>
            <p className="about-description">
              As a leading provider of technology products, TechFocus has become a trusted source for IT hardware products and solutions. We offer a comprehensive range of electronic and hardware items, such as computers, monitors, peripheral products, and networking equipment, making it easy for customers to acquire the technology they need.
            </p>
            <p className="about-mission">
              Our mission is to exceed our government customers' expectations while maintaining an unparalleled level of service in the professional services industry.
            </p>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
