import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import Logo from './Logo';
import './Header.css';

const Header = () => {
  const location = useLocation();
  const isHomePage = location.pathname === '/';

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <Logo />
          <nav className="nav">
            <ul className="nav-list">
              <li><Link to="/about" className={location.pathname === '/about' ? 'active' : ''}>About</Link></li>
              <li><Link to="/solution" className={location.pathname === '/solution' ? 'active' : ''}>Solution</Link></li>
              <li><a href={isHomePage ? "#contract" : "/#contract"}>Contract vehicles</a></li>
              <li><a href={isHomePage ? "#employee" : "/#employee"}>Employee portal</a></li>
              <li><a href={isHomePage ? "#careers" : "/#careers"}>Careers</a></li>
              <li><a href={isHomePage ? "#news" : "/#news"}>News</a></li>
            </ul>
          </nav>
          <Link to={isHomePage ? "#contract" : "/#contract"} className="btn btn-primary">
            Contract
          </Link>
        </div>
      </div>
    </header>
  );
};

export default Header;
